import React, { useEffect } from "react";

function OfferCard({ offer }) {
  const { id, name, style, description, time } = offer;

  function addDanceClass(id) {
    useEffect(() => {
      fetch("http://localhost:3000/dance_classes");
    });
  }

  return (
    <div>
      <br></br>
      <br></br>
      <h4>{name}</h4>
      <h5>{style}</h5>
      <p>{description}</p>
      <p>{time}</p>
      <button
        className="px-4 py-1 bg-red"
        onClick={() => addDanceClass(offer.id)}
      >
        Sign Up
      </button>
      <br></br>
    </div>
  );
}

export default OfferCard;
