import React, { useState, useEffect } from "react";
import ProfileDeleteCard from "./ProfileDeleteCard";

function ProfileCard({ currentUser, setCurrentUser }) {
  const [classes, setClasses] = useState(currentUser.dance_classes);

  const {
    id,
    username,
    admin,
    first_name,
    last_name,
    address,
    phone,
    dance_classes,
  } = currentUser;

  const danceClassesProfile = currentUser.dance_classes;

  const handleDelete = () => {
    fetch(`http://localhost:3000/dance_classes/`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: currentUser.name,
        style: currentUser.style,
        description: currentUser.description,
        user_id: null,
        teacher_id: currentUser.teacher_id,
      }),
    });
  };

  return (
    <div>
      <br></br>
      <h1>Welcome back, {currentUser.first_name}!</h1>
      <h3>Classes:</h3>
      {classes.map((danceClass) => (
        <ProfileDeleteCard />
      ))}
    </div>
  );
}

export default ProfileCard;
