import React, { useState, useEffect } from "react";

function ProfileCard({ currentUser, danceClass }) {
  const [classId, setClassId] = useState("");
  const {
    id,
    username,
    admin,
    first_name,
    last_name,
    address,
    phone,
    dance_classes,
  } = currentUser;

  useEffect(() => {
    setClassId(danceClass);
  }, [classId]);

  return (
    <div>
      <br></br>
      <h1>Welcome back, {currentUser.first_name}!</h1>
      {dance_classes.map((danceClass) => (
        <div key={danceClass.id}>
          <p>{danceClass.name}</p>
        </div>
      ))}
    </div>
  );
}

export default ProfileCard;
