import React, { useState, useEffect } from "react";

function ProfileCard({ currentUser }) {
  const [danceClassId, setDanceClassId] = useState("");
  const [classes, setClasses] = useState(currentUser.dance_classes);
  const [classId, setClassId] = useState("");
  const {
    id,
    username,
    admin,
    first_name,
    last_name,
    address,
    phone,
    dance_classes,
  } = currentUser;
  console.log(updatedClasses);
  const handleDelete = (danceClassId) => {
    setClassId(danceClassId);
    fetch(`http://localhost:3000/dance_classes/${danceClassId}`, {
      method: "DELETE",
    }).then(() => {
      const updatedClasses = classes.filter(
        (danceClass) => danceClass.id !== danceClassId
      );
      setClasses(updatedClasses);
    });
  };

  return (
    <div>
      <br></br>
      <h1>Welcome back, {currentUser.first_name}!</h1>
      <h3>Classes:</h3>
      {classes.map((danceClass) => (
        <div key={danceClass.id}>
          <p>{danceClass.name}</p>
          <button
            class="btn btn-secondary"
            type="button"
            onClick={() => handleDelete(currentUser.dance_classes[0].id)}
          >
            Drop Class
          </button>
        </div>
      ))}
    </div>
  );
}

export default ProfileCard;
