import React, { useState, useEffect } from "react";

function ProfileCard({ currentUser }) {
  const [classes, setClasses] = useState(currentUser.danceClasses);
  const [classId, setClassId] = useState("");
  const {
    id,
    username,
    admin,
    first_name,
    last_name,
    address,
    phone,
    dance_classes,
  } = currentUser;

  useEffect(() => {
    setClasses(danceClasses);
  });
  const danceClasses = currentUser.dance_classes;
  console.log(danceClasses);
  const handleDelete = (danceClassId) => {
    fetch(`http://localhost:3000/dance_classes/${danceClassId}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: danceClasses.name,
        style: danceClasses.style,
        description: danceClasses.description,
        user_id: null,
        teacher_id: danceClasses.teacher_id,
      }),
    });
  };

  return (
    <div>
      <br></br>
      <h1>Welcome back, {currentUser.first_name}!</h1>
      <h3>Classes:</h3>
      {classes.map((danceClass) => (
        <div key={danceClass.id}>
          {danceClass ? <p>{danceClass.name}</p> : null}
          <button
            class="btn btn-secondary"
            type="button"
            onClick={() => handleDelete(currentUser.dance_classes[0].id)}
          >
            Drop Class
          </button>
        </div>
      ))}
    </div>
  );
}

export default ProfileCard;
